处理所有的contributions 并将任何新倒入的属性源应用到环境中 void processAndApply() {
     ConfigDataImporter importer = new ConfigDataImporter(this.logFactory, this.notFoundAction, this.resolvers,
             this.loaders);
     registerBootstrapBinder(this.contributors, null, DENY_INACTIVE_BINDING);
     ConfigDataEnvironmentContributors contributors = processInitial(this.contributors, importer);
     ConfigDataActivationContext activationContext = createActivationContext(
             contributors.getBinder(null, BinderOption.FAIL_ON_BIND_TO_INACTIVE_SOURCE));
     contributors = processWithoutProfiles(contributors, importer, activationContext);
     activationContext = withProfiles(contributors, activationContext);
     contributors = processWithProfiles(contributors, importer, activationContext);
     applyToEnvironment(contributors, activationContext, importer.getLoadedLocations(),
             importer.getOptionalLocations());
 }



    private ConfigDataEnvironmentContributors processInitial(ConfigDataEnvironmentContributors contributors,
            ConfigDataImporter importer) {
        this.logger.trace("Processing initial config data environment contributors without activation context");
        contributors = contributors.withProcessedImports(importer, null);
        registerBootstrapBinder(contributors, null, DENY_INACTIVE_BINDING);
        return contributors;
    }

    ConfigDataEnvironmentContributors withProcessedImports(ConfigDataImporter importer,
            ConfigDataActivationContext activationContext) {
        ImportPhase importPhase = ImportPhase.get(activationContext);
        this.logger.trace(LogMessage.format("Processing imports for phase %s. %s", importPhase,
                (activationContext != null) ? activationContext : "no activation context"));
        ConfigDataEnvironmentContributors result = this;
        int processed = 0;
        while (true) {
            ConfigDataEnvironmentContributor contributor = getNextToProcess(result, activationContext, importPhase);
            if (contributor == null) {
                this.logger.trace(LogMessage.format("Processed imports for of %d contributors", processed));
                return result;
            }
            if (contributor.getKind() == Kind.UNBOUND_IMPORT) {
                ConfigDataEnvironmentContributor bound = contributor.withBoundProperties(result, activationContext);
                result = new ConfigDataEnvironmentContributors(this.logger, this.bootstrapContext,
                        result.getRoot().withReplacement(contributor, bound));
                continue;
            }
            ConfigDataLocationResolverContext locationResolverContext = new ContributorConfigDataLocationResolverContext(
                    result, contributor, activationContext);
            ConfigDataLoaderContext loaderContext = new ContributorDataLoaderContext(this);
            List imports = contributor.getImports();
            this.logger.trace(LogMessage.format("Processing imports %s", imports));
            Map imported = importer.resolveAndLoad(activationContext,
                    locationResolverContext, loaderContext, imports);
            this.logger.trace(LogMessage.of(() -> getImportedMessage(imported.keySet())));
            ConfigDataEnvironmentContributor contributorAndChildren = contributor.withChildren(importPhase,
                    asContributors(imported));
            result = new ConfigDataEnvironmentContributors(this.logger, this.bootstrapContext,
                    result.getRoot().withReplacement(contributor, contributorAndChildren));
            processed++;
        }
    }

解析并加载给定的位置列表，过滤掉已经加载过的位置
    Map resolveAndLoad(ConfigDataActivationContext activationContext,
            ConfigDataLocationResolverContext locationResolverContext, ConfigDataLoaderContext loaderContext,
            List locations) {
        try {
            Profiles profiles = (activationContext != null) ? activationContext.getProfiles() : null;
            //YamlPropertySourceLoader、PropertiesPropertySourceLoader getFileExtensions方法
            //推断出可以加载DEFAULT_SEARCH_LOCATIONS路径下的哪些配置文件，最终筛选出实际存在的配置文件
            List resolved = resolve(locationResolverContext, profiles, locations);
            //使用合适的ConfigDataLoader解析配置文件，得到ConfigData
            return load(loaderContext, resolved);
        }
        catch (IOException ex) {
            throw new IllegalStateException("IO error on loading imports from " + locations, ex);
        }
    }

